mapper.select:
select * from lession_info

	del_flag=#{DEL_FLAG_NORMAL}

		and name like concat("%",#{name},"%")


		and text_id=#{textId}


		and unit =#{unit}


	and text_id=#{textId}


order by text_id desc,unit asc

select count(1) from
lession_info where name = #{name} and
del_flag=#{DEL_FLAG_NORMAL}
