mapper.select:
SELECT
*
FROM class_info where id=#{id} and del_flag=#{DEL_FLAG_NORMAL}

SELECT
*
FROM class_info

	del_flag=#{DEL_FLAG_NORMAL}

		and grade_id=#{gradeId}


ORDER BY create_time

select * from class_info

	del_flag=#{DEL_FLAG_NORMAL}

		and name=#{name}


		and grade_id=#{gradeId}


ORDER BY create_time
