mapper.select:
select * from user

	del_flag=#{DEL_FLAG_NORMAL}

		and phonenum=#{phonenum}


		and loginname like concat('%',#{loginname},'%')


		and nickname like concat('%' ,#{nickname},'%')


		and class_id = #{classId}


		and realname like concat('%',#{realname},'%')


order by create_time desc
