mapper.select:
SELECT u.realname, ci.NAME, ( SELECT count(1) FROM lession_class_bind WHERE del_flag = #{DEL_FLAG_NORMAL} AND class_id = ci.id ), ( SELECT count(1) FROM studio_info WHERE del_flag = #{DEL_FLAG_NORMAL} AND user_id = u.id AND lession_id IN ( SELECT lession_id FROM lession_class_bind WHERE del_flag = #{DEL_FLAG_NORMAL} AND class_id = ci.id ) ), ( SELECT count(1) FROM studio_info WHERE lession_id IN ( SELECT lession_id FROM lession_class_bind WHERE class_id = ci.id AND del_flag = #{DEL_FLAG_NORMAL} ) AND del_flag = #{DEL_FLAG_NORMAL} AND pointer IS NOT NULL AND point >= 8 AND point <= 10 AND user_id = u.id ) AS num_a, ( SELECT count(1) FROM studio_info WHERE lession_id IN ( SELECT lession_id FROM lession_class_bind WHERE class_id = ci.id AND del_flag = #{DEL_FLAG_NORMAL} ) AND del_flag = #{DEL_FLAG_NORMAL} AND pointer IS NOT NULL AND point >= 4 AND point <= 7 AND user_id = u.id ) AS num_p, ( SELECT count(1) FROM studio_info WHERE lession_id IN ( SELECT lession_id FROM lession_class_bind WHERE class_id = ci.id AND del_flag = #{DEL_FLAG_NORMAL} ) AND del_flag = #{DEL_FLAG_NORMAL} AND pointer IS NOT NULL AND point >= 0 AND point <= 3 AND user_id = u.id ) AS num_e, ( SELECT avg(point) FROM studio_info WHERE user_id = u.id AND del_flag = #{DEL_FLAG_NORMAL} AND pointer IS NOT NULL ), ( SELECT count(1) FROM `user` u2 WHERE class_id = ci.id AND del_flag = #{DEL_FLAG_NORMAL} AND ( SELECT avg(point) FROM studio_info WHERE user_id = u2.id AND del_flag = #{DEL_FLAG_NORMAL} AND pointer IS NOT NULL ) > ( ( SELECT avg(point) FROM studio_info WHERE user_id = u.id AND del_flag = #{DEL_FLAG_NORMAL} AND pointer IS NOT NULL ) ) ), ( SELECT count(1) FROM `user` WHERE class_id = ci.id AND del_flag = #{DEL_FLAG_NORMAL} ) FROM `user` u JOIN class_info ci ON ci.id = u.class_id
u.del_flag = #{DEL_FLAG_NORMAL} AND ci.del_flag = #{DEL_FLAG_NORMAL}
AND u.realname like concat('%' , #{userName},'%')
AND ci.NAME = #{className}
